CREATE SCHEMA wedsite;CREATE TABLE wedsite.invite_details (id SERIAL PRIMARY KEY,display_name VARCHAR NOT NULL,access_code VARCHAR NOT NULL,email VARCHAR);CREATE TABLE wedsite.people (id SERIAL PRIMARY KEY,name VARCHAR NOT NULL,rsvp_status VARCHAR NOT NULL DEFAULT 'NO_REPLY',dietary_requirements VARCHAR,message VARCHAR,evening_only BOOLEAN NOT NULL,invite_details_id INTEGER REFERENCES wedsite.invite_details(id));CREATE TABLE wedsite.accomodation (id SERIAL PRIMARY KEY,name VARCHAR NOT NULL,location_name VARCHAR NOT NULL,distance DECIMAL NOT NULL,website_link VARCHAR NOT NULL,approx_price INTEGER NOT NULL,wifi VARCHAR,parking VARCHAR,check_in VARCHAR,check_out VARCHAR,latitude DECIMAL NOT NULL,longitude DECIMAL NOT NULL);CREATE SCHEMA IF NOT EXISTS core;CREATE TABLE core.application_config(id SERIAL PRIMARY KEY,key VARCHAR NOT NULL,value VARCHAR NOT NULL,application VARCHAR NOT NULL,refresh_rate BIGINT NOT NULL);CREATE TABLE core.site_config(id SERIAL PRIMARY KEY,key VARCHAR NOT NULL,value VARCHAR NOT NULL,site VARCHAR NOT NULL,refresh_rate BIGINT NOT NULL);CREATE TABLE core.api_applications (id SERIAL PRIMARY KEY,name VARCHAR NOT NULL UNIQUE,domain VARCHAR,description VARCHAR,git_repo VARCHAR NOT NULL,running_port INT,upgrade_port INT,internal_port INT);CREATE TABLE core.logs (id SERIAL PRIMARY KEY,application_name VARCHAR NOT NULL,application_version VARCHAR(32),logger_name VARCHAR NOT NULL,message VARCHAR NOT NULL,severity VARCHAR(32) NOT NULL,timestamp TIMESTAMP NOT NULL);DROP TABLE core.site_config;DROP TABLE core.application_config;DROP TABLE core.api_applications;CREATE TABLE core.config(id SERIAL PRIMARY KEY,key VARCHAR NOT NULL,value VARCHAR NOT NULL,application_name VARCHAR NOT NULL,refresh_rate BIGINT NOT NULL);